import * as actions from "../actions/Types";

const CartReducer = (state: any = [], action: any) => {
  switch (action.type) {
    case actions.ADD_TO_CART:
      state = [...state, action.payload];
      console.log("DDD", state);
      return state;

    case actions.REMOVE_FROM_CART:
      if (state.isbn.findIndex((item: any) => item === action.payload) !== -1) {
        state.isbn = state.isbn.filter((isbn: any) => isbn !== action.payload);
        return state;
      }
      state.isbn = [...state.isbn, action.payload];
      return state;

    default:
      return state;
  }
};

export default CartReducer;
