import "./scss/ShoppingCart.scss";
import { DataGrid } from "@mui/x-data-grid";
import { Button } from "@mui/material";
import { useEffect, useState } from "react";

const columns = [
  {
    field: "id",
    headerName: "",
    width: 150,
    editable: true,
  },
  {
    field: "cover",
    headerName: "",
    width: 150,
    editable: true,
  },
  {
    field: "title",
    headerName: "Titre",
    width: 150,
    editable: true,
  },

  {
    field: "price",
    headerName: "Prix",
    type: "number",
    width: 110,
    editable: true,
  },
];

const rows = [
  { id: 1, lastName: "Snow", firstName: "Jon", age: 35 },
  { id: 2, lastName: "Lannister", firstName: "Cersei", age: 42 },
  { id: 3, lastName: "Lannister", firstName: "Jaime", age: 45 },
  { id: 4, lastName: "Stark", firstName: "Arya", age: 16 },
  { id: 5, lastName: "Targaryen", firstName: "Daenerys", age: null },
  { id: 6, lastName: "Melisandre", firstName: null, age: 150 },
  { id: 7, lastName: "Clifford", firstName: "Ferrara", age: 44 },
  { id: 8, lastName: "Frances", firstName: "Rossini", age: 36 },
  { id: 9, lastName: "Roxie", firstName: "Harvey", age: 65 },
];

const ShoppingCart = () => {
  const [books, setBooks] = useState([]);
  useEffect(() => {
    fetch("https://henri-potier.techx.fr/books")
      .then((res) => res.json())
      .then((result) => {
        result
          .map((row: any) => {
            const { isbn, ...rest } = row;
            return { id: isbn, ...rest };
          })
          .then((res) => {
            console.log(res);
          });

        setBooks(result);
      });
  });
  return (
    <div className="shoppingCart">
      <div className="shoppingCart__left">
        <h3>Votre panier:</h3>
        <div style={{ height: 400, width: "100%" }}>
          <DataGrid
            rows={books}
            columns={columns}
            pageSize={5}
            rowsPerPageOptions={[5]}
            checkboxSelection
            disableSelectionOnClick
          />
        </div>
      </div>
      <div className="shoppingCart__right">
        <div className="shoppingCart__box">
          <h3>Sous-total: 30</h3>
          <Button variant="contained">Passer la commande</Button>
        </div>
      </div>
    </div>
  );
};

export default ShoppingCart;
