import "./scss/ShoppingCart.scss";
import { DataGrid } from "@mui/x-data-grid";
import { Button } from "@mui/material";
import { useEffect, useState } from "react";
import { connect } from "react-redux";
import { removeFromCart } from "../components/actions/CartActions";

const columns = [
  {
    field: "title",
    headerName: "Titre",
    width: 350,
    editable: true,
  },

  {
    field: "price",
    headerName: "Prix",
    type: "number",
    width: 70,
    editable: true,
  },
];

const ShoppingCart = (props: any) => {
  const [removedBook, setRemovedBooks] = useState([]);
  const [totalPriceBeforeDiscount, setTotalPriceBeforeDiscount] = useState(0);
  // const [offers, setOffers] = useState([]);

  //handle remove from cart
  const handleDelete = () => {
    props.dispatch(removeFromCart(removedBook));
  };

  //Loop through the state books to find the prices and the books id
  const getBooksDetails = () => {
    let total: number = 0;
    let bookId: any = [];

    props.Cart.map((book: any) => {
      total += book.price;
      bookId.push(book.id);
    });

    setTotalPriceBeforeDiscount(total);
    getBooksId(bookId);
  };

  //Stringify and concatenate the ids (isbn) and call the getDiscount method
  const getBooksId = (books: []) => {
    let book = books.join();
    if (book) {
      getDiscount(book);
    }
  };

  //Call the commercial offers API
  const getDiscount = (selectedBooksId: string) => {
    let url = `https://henri-potier.techx.fr/books/${selectedBooksId}/commercialOffers`;

    fetch(url)
      .then((res) => res.json())
      .then((result) => {
        console.log("TEST");
        return result.offers;
      })
      .then((offer: any) => calculateBestOffer(offer));
  };

  const calculateBestOffer = (offers: any) => {
    console.log("TEST 2", offers);
    // Calculate the final price with all of the 3 offers applied (under certain conditions)
    let bestOffer = 0;

    //Percentage
    if (offers.find((x: any) => x.type === "percentage")) {
      let percentageOffer = offers.find((x: any) => x.type === "percentage");
      bestOffer = totalPriceBeforeDiscount * (percentageOffer.value / 100);
    }

    //Minus
    if (offers.find((x: any) => x.type === "minus")) {
      let minusOffer = offers.find((x: any) => x.type === "minus");
      if (bestOffer != 0) {
        // let total = totalPriceBeforeDiscount - minusOffer;

        if (minusOffer < minusOffer) {
          bestOffer = minusOffer;
        }
      } else {
        bestOffer = minusOffer;
      }
    }

    //Slice
    if (offers.find((x: any) => x.type === "slice")) {
      let sliceOffer = offers.find((x: any) => x.type === "slice");
      if (sliceOffer.sliceValue < totalPriceBeforeDiscount) {
        let slicedNum = Math.floor(
          totalPriceBeforeDiscount / sliceOffer.sliceValue
        );

        bestOffer = slicedNum * offers.value;
        // if (condition) {
        //   bestOffer = totalPriceBeforeDiscount - sliceOffer;
        // }
      }
    }
  };

  useEffect(() => {
    getBooksDetails();
  }, [props.Cart]);

  return (
    <div className="shoppingCart">
      <div className="shoppingCart__left">
        <div className="shoppingCart__title">
          <h3>Votre panier:</h3>
        </div>

        <div className="shoppingCart__grid">
          <DataGrid
            rows={props.Cart}
            columns={columns}
            pageSize={5}
            rowsPerPageOptions={[5]}
            checkboxSelection
            disableSelectionOnClick
            onSelectionModelChange={(selected: any) =>
              setRemovedBooks(selected)
            }
          />
          <Button onClick={handleDelete}>Delete</Button>
        </div>
      </div>
      <div className="shoppingCart__right">
        <div className="shoppingCart__box">
          <h3>Nos offres commerciales</h3>

          <h3>Sous-total: {totalPriceBeforeDiscount}</h3>
          <Button variant="contained">Passer la commande</Button>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state: any) => {
  return {
    Cart: state.CartReducer,
  };
};

export default connect(mapStateToProps)(ShoppingCart);
