import "./scss/ShoppingCart.scss";
import { DataGrid } from "@mui/x-data-grid";
import { Button } from "@mui/material";
import { useEffect, useState } from "react";

const columns = [
  {
    field: "title",
    headerName: "Titre",
    width: 200,
    editable: true,
  },

  {
    field: "price",
    headerName: "Prix",
    type: "number",
    width: 110,
    editable: true,
  },
];

const ShoppingCart = () => {
  const [books, setBooks] = useState([]);
  useEffect(() => {
    fetch("https://henri-potier.techx.fr/books")
      .then((res) => res.json())
      .then((result) => {
        //This is to map isbn to id in order to have a unique key
        return result.map((row: any) => {
          const { isbn, ...rest } = row;
          return { id: isbn, ...rest };
        });
      })
      .then((res: any) => {
        setBooks(res);
      });
  });
  return (
    <div className="shoppingCart">
      <div className="shoppingCart__left">
        <h3>Votre panier:</h3>
        <div style={{ height: 400, maxWidth: 500, justifyContent: "center" }}>
          <DataGrid
            rows={books}
            columns={columns}
            pageSize={5}
            rowsPerPageOptions={[5]}
            checkboxSelection
            disableSelectionOnClick
          />
        </div>
      </div>
      <div className="shoppingCart__right">
        <div className="shoppingCart__box">
          <h3>Sous-total: 30</h3>
          <Button variant="contained">Passer la commande</Button>
        </div>
      </div>
    </div>
  );
};

export default ShoppingCart;
