import * as actions from "../actions/Types";

const CartReducer = (state: any = [], action: any) => {
  switch (action.type) {
    case actions.ADD_TO_CART:
      state = [...state, action.payload];

      let parsedState = state.map((row: any) => {
        const { isbn, ...rest } = row;
        return { id: isbn, ...rest };
      });

      return parsedState;

    case actions.REMOVE_FROM_CART:
      console.log("Hit removed", action);
      if (state.id.findIndex((item: any) => item === action.payload) !== -1) {
        state.id = state.isbn.filter((id: any) => id !== action.payload);
        return state;
      }
      state.id = [...state.id, action.payload];
      return state;

    default:
      console.log("default", state);
      return state;
  }
};

export default CartReducer;
