          defaultValue=""
          onChange={onchangeText}
          fullWidth
        />
        <div className="mainPage__filterBtn">
          <IconButton>
            <FilterListIcon />
          </IconButton>
        </div>
      </div> */}

      <div className="mainPage__cardContainer">
        {filterSearch.map((book: Book, index: number) => (
          <LibraryCard book={book} index={index} />
        ))}
      </div>
    </div>
  );
};

export default MainPage;
