import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import "./MainPage.scss";
import { Button, IconButton, TextField } from "@mui/material";
import FilterListIcon from "@mui/icons-material/FilterList";
import LibraryCard from "../components/LibraryCard";

type Book = {
  isbn: string;
  title: string;
  price: number;
  cover: string;
  synopsis: string;
};

const MainPage = () => {
  const [books, setBooks] = useState([]);
  const [searchText, setSearchText] = useState();

  useEffect(() => {
    getBooks();
  }, []);

  const getBooks = async () => {
    fetch("https://henri-potier.techx.fr/books")
      .then((res) => res.json())
      .then((result) => {
        setBooks(result);
      });
  };

  const filterSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    // setSearchText(inputText);

    const newData = books.filter((item: Book) => {
      if (e.target.value === "") {
        return getBooks();
      }

      item.title === e.target.value;

      // return itemData.indexOf(textData) > -1;
    });

    setBooks(newData);
  }; // End FilterSearch

  return (
    <div className="mainPage">
      <div className="mainPage__search">
        <TextField
          error={false}
          id="outlined-error"
          label="Recherche"
          defaultValue="Henri Potier à l'école des sorciers. . ."
          value={searchText}
          onChange={filterSearch()}
          fullWidth
        />
        <div className="mainPage__filterBtn">
          <IconButton>
            <FilterListIcon />
          </IconButton>
        </div>
      </div>

      <div className="cardContainer">
        {books.map((book: Book) => (
          <LibraryCard
            id={book.isbn}
            title={book.title}
            description={book.synopsis}
            cover={book.cover}
            price={book.price}
          />
        ))}
      </div>
    </div>
  );
};

export default MainPage;
