import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import "./scss/MainPage.scss";
import { Button, IconButton, TextField } from "@mui/material";
import FilterListIcon from "@mui/icons-material/FilterList";
import LibraryCard from "../components/LibraryCard";
import { connect } from "react-redux";
import { addToCart } from "../components/actions/CartActions";

type Book = {
  isbn: string;
  title: string;
  price: number;
  cover: string;
  synopsis: string;
};

const MainPage = (props: any) => {
  const [books, setBooks] = useState([]);
  const [searchText, setSearchText] = useState("");

  useEffect(() => {
    getBooks();
  }, []);

  const getBooks = async () => {
    fetch("https://henri-potier.techx.fr/books")
      .then((res) => res.json())
      .then((result) => {
        setBooks(result);
      });
  };

  const onchangeText = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchText(e.target.value);
  };

  const filterSearch = books.filter((book: Book) => {
    return book.title.toLowerCase().indexOf(searchText.toLowerCase()) !== -1;
  });

  return (
    <div className="mainPage">
      <div className="mainPage__search">
        <TextField
          error={false}
          id="outlined-error"
          label="Recherche"
          defaultValue=""
          onChange={onchangeText}
          fullWidth
        />
        <div className="mainPage__filterBtn">
          <IconButton>
            <FilterListIcon />
          </IconButton>
        </div>
      </div>

      <div className="mainPage__cardContainer">
        {filterSearch.map((book: Book) => (
          <LibraryCard
            id={book.isbn}
            title={book.title}
            description={book.synopsis}
            cover={book.cover}
            price={book.price}
          />
        ))}
      </div>
    </div>
  );
};

const mapStateToProps = (state: any) => {
  return {
    Cart: state.CartReducer,
  };
};

export default connect(mapStateToProps)(MainPage);
