import "./scss/ShoppingCart.scss";
import { DataGrid } from "@mui/x-data-grid";
import {
  Button,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
} from "@mui/material";
import { useEffect, useState } from "react";

const columns = [
  {
    field: "title",
    headerName: "Titre",
    width: 350,
    editable: true,
  },

  {
    field: "price",
    headerName: "Prix",
    type: "number",
    width: 70,
    editable: true,
  },
];

const ShoppingCart = () => {
  const [books, setBooks] = useState([]);
  useEffect(() => {
    fetch("https://henri-potier.techx.fr/books")
      .then((res) => res.json())
      .then((result) => {
        //This is to map isbn to id in order to have a unique key
        return result.map((row: any) => {
          const { isbn, ...rest } = row;
          return { id: isbn, ...rest };
        });
      })
      .then((res: any) => {
        setBooks(res);
      });
  });
  return (
    <div className="shoppingCart">
      <div className="shoppingCart__left">
        <div className="shoppingCart__title">
          <h3>Votre panier:</h3>
        </div>

        <div className="shoppingCart__grid">
          <DataGrid
            rows={books}
            columns={columns}
            pageSize={5}
            rowsPerPageOptions={[5]}
            checkboxSelection
            disableSelectionOnClick
          />
        </div>
      </div>
      <div className="shoppingCart__right">
        <div className="shoppingCart__box">
          <h3>Nos offres commerciales</h3>
          <FormControl fullWidth>
            <InputLabel id="demo-simple-select-label">Age</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={age}
              label="Age"
              onChange={handleChange}
            >
              <MenuItem value={10}>Ten</MenuItem>
              <MenuItem value={20}>Twenty</MenuItem>
              <MenuItem value={30}>Thirty</MenuItem>
            </Select>
          </FormControl>
          <h3>Sous-total: 30</h3>
          <Button variant="contained">Passer la commande</Button>
        </div>
      </div>
    </div>
  );
};

export default ShoppingCart;
